import React, { useState } from 'react';
import { ChevronDown, Plus, Search } from 'lucide-react';
import { apiService } from '../services/api';
import './FilterSection.css';

const FilterSection = ({ onFilterChange }) => {
  const [projectId, setProjectId] = useState('');
  const [status, setStatus] = useState('visit');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [isFilterMode, setIsFilterMode] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isFilterMode) {
      // Filter mode - update the filter
      onFilterChange({ projectId, status });
      setMessage('Filter applied successfully!');
      setTimeout(() => setMessage(''), 2000);
      return;
    }

    // Track mode - add new visitor
    if (!projectId || !status) {
      setMessage('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setMessage('');

      // Generate a random userId for tracking
      const randomUserId = 'user_' + Math.random().toString(36).substr(2, 9);
      await apiService.trackVisitor(projectId, randomUserId, status);
      setMessage('Visitor tracked successfully!');

      // Clear form after successful submission
      setTimeout(() => {
        setProjectId('');
        setStatus('visit');
        setMessage('');
      }, 2000);

    } catch (error) {
      setMessage('Failed to track visitor. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsFilterMode(!isFilterMode);
    setProjectId('');
    setStatus('visit');
    setMessage('');
  };

  return (
    <div className="filter-section">
      <div className="section-header">
        <div className="section-title">
          <h3>{isFilterMode ? 'Filter Visitors' : 'Track New Visitor'}</h3>
          <p>{isFilterMode ? 'Filter visitor data by Project ID and Status' : 'Add new visitor activity to the system'}</p>
        </div>
        <button type="button" className="mode-toggle-btn" onClick={toggleMode}>
          {isFilterMode ? (
            <>
              <Plus size={16} />
              Switch to Track
            </>
          ) : (
            <>
              <Search size={16} />
              Switch to Filter
            </>
          )}
        </button>
      </div>

      <form onSubmit={handleSubmit} className="filter-form">
        {message && (
          <div className={`message ${message.includes('successfully') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}

        <div className="filter-row">
          <div className="filter-field">
            <label htmlFor="projectId">Project ID</label>
            <input
              type="text"
              id="projectId"
              placeholder={isFilterMode ? "Filter by Project ID..." : "Enter Project ID"}
              value={projectId}
              onChange={(e) => setProjectId(e.target.value)}
              required={!isFilterMode}
            />
          </div>

          <div className="filter-field">
            <label htmlFor="status">{isFilterMode ? 'Status' : 'Action'}</label>
            <div className="select-wrapper">
              <select
                id="status"
                value={status}
                onChange={(e) => setStatus(e.target.value)}
                required={!isFilterMode}
              >
                {isFilterMode ? (
                  <>
                    <option value="">All Status</option>
                    <option value="visit">Visit</option>
                    <option value="click">Click</option>
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                    <option value="pending">Pending</option>
                  </>
                ) : (
                  <>
                    <option value="visit">Visit</option>
                    <option value="click">Click</option>
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                    <option value="pending">Pending</option>
                  </>
                )}
              </select>
              <ChevronDown size={16} className="select-arrow" />
            </div>
          </div>

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Processing...' : (
              <>
                {isFilterMode ? <Search size={16} /> : <Plus size={16} />}
                {isFilterMode ? 'Apply Filter' : 'Track Visitor'}
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default FilterSection;
